package ar.edu.unju.fi.ejercicio1;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Comentarios
		System.out.println("Ejercicio 1 de Programación Visual");
		// Declaración de variables y asignación
		
		int numero1 = 8, numero2 = 5, numero3 = 1, numero4 = 20, numero5= 9;
		
		// Proceso de obtener promedio
		double promedio = (numero1 + numero2 + numero3 + numero4 + numero5)/5.0;
		
		// Mostrar dato
		System.out.println(numero1+" "+numero2+" "+numero3+" "+numero4+" "+numero5);
		System.out.println("El promedio es: "+promedio);
	}

}
